(def example "#1 @ 1,3: 4x4\n#2 @ 3,1: 4x4\n#3 @ 5,5: 2x2")

(defn read-rectangle [input-string]
  (let [[id x1 y1 w d] (->> input-string
                            (re-seq #"[0-9]+")
                            (map #(Integer/parseInt %)))
        x2 (+ x1 w)
        y2 (+ y1 d)]
    (for [x (range x1 x2)
          y (range y1 y2)]
      {:id id :coordinate [x y]})))

(defn solution-1 [input-string]
  (->> input-string
       str/split-lines
       (mapcat read-rectangle)
       (map :coordinate)
       frequencies
       vals
       (filter #(> % 1))
       count))

(defn group-by-id
  [rectangles]
  (persistent!
    (reduce
      (fn [ret x]
        (let [v (get x :id)]
          (assoc! ret v (conj (get ret v []) (:coordinate x)))))
      (transient {}) rectangles)))

(defn solution-2 [input-string]
  (let [rectangles (->> input-string
                        str/split-lines
                        (mapcat read-rectangle))
        once (->> rectangles
                  (map :coordinate)
                  frequencies
                  (keep (fn [[k v]] (when (= 1 v) k)))
                  set)]
    (->> (group-by-id rectangles)
         (filter #(every? once (val %)))
         first
         key)))
